import "./calculadora.css";
import { useState } from "react";
import { pi, round } from "mathjs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowsLeft } from "@fortawesome/free-solid-svg-icons";


export function Calculator() {
  let [concat, setConcat] = useState("");
  const n = [
    "C",
    "+/-",
    "%",
    "/",
    7,
    8,
    9,
    "x",
    4,
    5,
    6,
    "-",
    1,
    2,
    3,
    "+",
    0,
    ".",
    "π",
    "=",
  ];

  return (

    <div className="cal-black">
      <label style={{ color: "white" }}>{concat}</label>
      <section className="conten-button">
        {n.map((e, i) => {
          return (
            <button
              onClick={() => {
                if (e === "=") {
                  setConcat(round(eval(concat), 4));
                } else if (e === "C") {
                  setConcat((concat = ""));
                } else if (e !== "C") {
                  if (e === "x") {
                    e = "*";
                  } else if (e === "π") {
                    e = round(pi, 5);
                  }
                  setConcat((concat += e));
                }
              }}
              key={i}
            >
              {e === "+/-" ?  <FontAwesomeIcon icon={faArrowsLeft}  className='arrow'/> : e}
            </button>
          );
        })}
      </section>
    </div>
  );
}
