    const [tecla, setTecla] = useState();
    const [concat, setCocant] = useState();
    const n = ['AC','+/-','%','/',7,8,9,'x',4,5,6, '-', 1,2,3,'+',0, '.','pi', '='];
    if(tecla === number){
        setCocant(tecla + tecla)
    }
    console.log(tecla);
    return <div className='cal-black'>
       <label>{concat}</label>
        <section className='conten-button'>
            {n.map((e,i)=>{
                return <button onClick={()=>{setTecla(e)}} key={i}>{e}</button>
            })}
        </section>
    </div>
}
