import './calculadora.css'
import { evaluate ,number,pi,re,round} from 'mathjs';
import { useState, useEffect } from 'react';


export function  Calculator(){
    const [result, setResultado] = useState();
    let [concat, setConcat] = useState('')
    const n = ['C','+/-','%','/',7,8,9,'x',4,5,6, '-', 1,2,3,'+',0, '.','pi', '='];
    useEffect(()=>{
        const clickButton =(e)=>{
            setConcat(concat += e)
            console.log(concat)
        }
    },[])
    return <div className='cal-black'>
       <label style={{color: 'white'}}>{concat}</label>
        <section className='conten-button'>
            {n.map((e,i)=>{
                return <button onClick={clickButton()} key={i}>{e}</button>
            })}
        </section>
    </div>
}
