import './calculadora.css'

import { useState} from 'react';


export function  Calculator(){
    let [concat, setConcat] = useState('')
    const [result, setResultado] = useState('');
    const n = ['C','+/-','%','/',7,8,9,'x',4,5,6, '-', 1,2,3,'+',0, '.','π', '='];

    return <div className='cal-black'>
       <label style={{color: 'white'}}>{result === '' ? concat: result}</label>
       <label style={{color: 'white'}}>{result}</label>
        <section className='conten-button'>
            {n.map((e,i)=>{
                return <button onClick={()=>{
                if(e === '='){
                    setResultado(eval(concat));
                    console.log(result)

                }else if(e === 'C'){
                    setConcat(concat = '')
                }else if(e !== 'C'){
                    setConcat(concat += e)}
                }
            } key={i}>{e}</button>
            })}
        </section>
    </div>
}
