import "./calculadora.css";
import { useState } from "react";
import { pi, round } from "mathjs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft } from "@fortawesome/free-solid-svg-icons";

export function Calculator() {
  let [concat, setConcat] = useState("");
  const n = ["C","DE","%","/",7,8,9,"x",4,5,6,"-",1,2,3,"+",0,".","π","=",];



  function ver(e) {
    return e === "<-" ? (
      <FontAwesomeIcon icon={faArrowLeft} className="arrow" />
    ) : (
      e
    );
  }
  return (
    <div className="cal-black">
      <section className="cabecera-cal">
        <input className="vist-resul" readOnly value={concat}/>
      </section>
      <section className="conten-button">
        {n.map((e, i) => {
          return (
            <button
              onClick={() => {
                if (e === "=") {
                  setConcat(round(eval(concat), 4));
                } else if (e === "C") {
                  setConcat((concat = ""));
                } else if (e !== "C" && e !== "DE") {
                  if (e === "x") {
                    e = "*";
                  } else if (e === "π") {
                    e = round(pi, 3);
                  }
                  setConcat((concat += e));
                } else if (e === "DE") {
                  setConcat(concat.slice(0, concat.length - 1));
                }
              }}
              key={i}
            >
              {ver(e)}
            </button>
          );
        })}
      </section>
    </div>
  );
}
