import './calculadora.css'
import { useState, useEffect} from 'react';
import { pi, round } from 'mathjs';


export function  Calculator(){
    let [concat, setConcat] = useState('')
    let [result, setResultado] = useState('');
    const n = ['C','+/-','%','/',7,8,9,'x',4,5,6, '-', 1,2,3,'+',0, '.',round(pi, 5), '='];



    return <div className='cal-black'>
       <label style={{color: 'white'}}>{concat}</label>
        <section className='conten-button'>
            {n.map((e,i)=>{
                return <button onClick={()=>{
                if(e === '='){
                    setConcat(eval(concat))
                }else if(e === 'C'){
                    setConcat(concat = '')
                }else if(e !== 'C'){
                    if(e=== 'x'){
                        e = '*'                    }
                    setConcat(concat += e)
                }
                }
            } key={i}>{e}</button>
            })}
        </section>
    </div>
}
